#Convert URL to tibble

url_to_tibble &lt;- function(url = NULL) {
    raw_data &lt;- httr::GET(url)
    parsed_data &lt;- fromJSON(rawToChar(raw_data$content))
    return (as_tibble(parsed_data))
}

